import React, { useState, useEffect } from 'react';
import './style.css';
import { useNavigate } from 'react-router-dom';

const OrderHistory = () => {
  const [email, setEmail] = useState(() => window.sessionStorage.getItem('email'));
  const [userTickets, setUserTickets] = useState([]);
  const [winningNumbers, setWinningNumbers] = useState({});
  const navigate = useNavigate();

  const goHome = () => {
    navigate('/Home');
  };

  const fetchUserTickets = async () => {
    try {
      const response = await fetch('http://localhost:3080/get-user-tickets', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });

      if (response.ok) {
        const { tickets } = await response.json();
        setUserTickets(tickets);
      } else {
        console.error('Failed to fetch user tickets');
      }
    } catch (error) {
      console.error('Error fetching user tickets', error);
    }
  };

  // email is set at the beginning so useEffect immediately runs and fetches the user's tickets lists them.
  useEffect(() => {
    fetchUserTickets();
    const fetchWinningNumbers = async () => {
      try {
        const response = await fetch('http://localhost:3080/get-winning-numbers');
        if (response.ok) {
          setWinningNumbers(await response.json());
        }
      } catch (error) {
        console.error('Error fetching winning numbers:', error);
      }
    };
    fetchWinningNumbers();
  }, [email]);

  const isWinner = (ticket) => {
    const winNums = winningNumbers[ticket.type.toLowerCase().replace(/ /g, '')];
    if (!winNums) return false;
    const sortedTicket = [...ticket.numbers].sort((a, b) => a - b);
    const sortedWin = [...winNums].sort((a, b) => a - b);
    return sortedTicket.every((num, i) => num === sortedWin[i]);
  };

  //iterates over userTickets and creates a list item for each number and type
  const myList = userTickets.map((item, index) => (
    <li key={index}>
      {item.type} {item.numbers.join(', ')}: {isWinner(item) ? 'Winner!' : 'Not a Winning Number'}
    </li>
  ));

  return (
    <div>
      <div className="topnav">
        <div className="homeRoute">
          <button className="button" onClick={goHome}>
            Home
          </button>
        </div>
      </div>

      <h1>Order History</h1>

      <div className="order-history">
        <h2>Your Lottery Tickets:</h2>
        <ul>{myList}</ul>
      </div>
    </div>
  );
};

export default OrderHistory;
